package ps.g49.socialroutingservice.repositories

import ps.g49.socialroutingservice.model.Person
import ps.g49.socialroutingservice.model.Route

interface PersonRepository {

    /**
     * @param identifier is generated by the database or the email
     */
    fun findPersonById(identifier: String): Person

    fun findUserPerformedRoutes(identifier: String): List<Route>

    fun findUserCreatedRoutes(identifier: String): List<Route>

}